import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { logout } from './actions/userActions';

import Navbar from './components/Navbar';
import Cart from './components/Cart';
import Login from './components/Login';
import Register from './components/Register';
import ProductList from './components/ProductList';
import ProductScreen from './screens/ProductScreen';  // Assuming this is the detailed view of a single product
import OrderHistory from './components/OrderHistory';
import ShippingScreen from './screens/ShippingScreen'; // Assume you'll have a shipping page for checkout
import PaymentScreen from './screens/PaymentScreen'; // Payment step during checkout
import PlaceOrderScreen from './screens/PlaceOrderScreen'; // Final step in the order process

const App = () => {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  useEffect(() => {
    // Any effect that might be needed when the app initializes or re-renders based on certain conditions.
  }, [dispatch, userInfo]);

  return (
    <Router>
      <div>
        {/* The Navbar will be present on every page */}
        <Navbar />

        {/* Define routes for different parts of the application */}
        <Switch>
          <Route path="/" component={ProductList} exact />
          <Route path="/login" component={Login} />
          <Route path="/register" component={Register} />
          <Route path="/product/:id" component={ProductScreen} />
          <Route path="/cart/:id?" component={Cart} />  {/* Optional product id */}
          <Route path="/orderhistory" component={OrderHistory} />

          {/* Checkout Process Steps */}
          <Route path="/shipping" component={ShippingScreen} />
          <Route path="/payment" component={PaymentScreen} />
          <Route path="/placeorder" component={PlaceOrderScreen} />

          {/* Redirect if no match */}
          <Route component={ProductList} />
        </Switch>
      </div>
    </Router>
  );
};

export default App;
